mapper.delete:
DELETE FROM Users
WHERE USER_ID IN

    #{id}

mapper.select:
SELECT USER_ID as userId, USER_NAME as userName, AUTHORITY_NAME as authorityName FROM users WHERE 1=1
AND USER_NAME LIKE CONCAT('%', #{userName}, '%')
AND AUTHORITY_NAME = #{authorityName}
ORDER BY USER_ID LIMIT #{pageStart}, #{perPageNum}

SELECT COUNT(*) FROM Users WHERE 1=1
AND USER_NAME LIKE CONCAT('%', #{userName}, '%')
AND AUTHORITY_NAME = #{authorityName}

SELECT n.*, a.* FROM notices n LEFT JOIN file_attachments a ON n.SEQ = a.SEQ WHERE 1=1
AND n.POST_DATE = #{postDate}
AND n.TITLE LIKE CONCAT('%', #{title}, '%')
AND n.CONTENT LIKE CONCAT('%', #{content}, '%')
ORDER BY n.SEQ LIMIT #{offset}, #{size}

SELECT IDENTIFIER, TYPE, STORAGE_LOCATION, FILE_NAME, CREATED_DATE
FROM file_attachments
WHERE SEQ = #{seq}
